import React, { useState, useEffect, useRef } from 'react';

const WORK_DURATION = 25 * 60; // 25 минут в секундах
const BREAK_DURATION = 5 * 60; // 5 минут

export default function PomodoroTimer() {
  const [secondsLeft, setSecondsLeft] = useState(WORK_DURATION);
  const [isRunning, setIsRunning] = useState(false);
  const [isWork, setIsWork] = useState(true);
  const intervalRef = useRef(null);

  useEffect(() => {
    if (isRunning) {
      intervalRef.current = setInterval(() => {
        setSecondsLeft((prev) => {
          if (prev === 1) {
            clearInterval(intervalRef.current);
            setIsRunning(false);
            setIsWork((prevMode) => !prevMode);
            return isWork ? BREAK_DURATION : WORK_DURATION;
          }
          return prev - 1;
        });
      }, 1000);
    }

    return () => clearInterval(intervalRef.current);
  }, [isRunning, isWork]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="flex flex-col items-center bg-gray-100 p-6 rounded shadow w-64 mx-auto mt-10">
      <h2 className="text-xl font-semibold mb-4">{isWork ? 'Work' : 'Break'}</h2>
      <div className="text-5xl font-mono mb-6">{formatTime(secondsLeft)}</div>
      <div className="flex space-x-2">
        {!isRunning ? (
          <button
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
            onClick={() => setIsRunning(true)}
          >
            Start
          </button>
        ) : (
          <button
            className="bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
            onClick={() => {
              clearInterval(intervalRef.current);
              setIsRunning(false);
            }}
          >
            Pause
          </button>
        )}
        <button
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
          onClick={() => {
            clearInterval(intervalRef.current);
            setIsRunning(false);
            setSecondsLeft(isWork ? WORK_DURATION : BREAK_DURATION);
          }}
        >
          Reset
        </button>
      </div>
    </div>
  );
}
