import React, { useState } from 'react';
import { FaEdit, FaTrash, FaCheck, FaForward, FaPlus } from 'react-icons/fa';

const defaultMorning = [
  { id: 1, name: 'Wash your face', done: false, skipped: false },
  { id: 2, name: 'Make your bed', done: false, skipped: false },
  { id: 3, name: 'Do morning exercise', done: false, skipped: false },
  { id: 4, name: 'Drink a glass of water', done: false, skipped: false },
  { id: 5, name: 'Meditate', done: false, skipped: false },
];

export const Routines = () => {
  const [morningRoutines, setMorningRoutines] = useState(defaultMorning);
  const [eveningRoutines, setEveningRoutines] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [newMorningText, setNewMorningText] = useState('');
  const [newEveningText, setNewEveningText] = useState('');
  const [showMessage, setShowMessage] = useState(false);

  const handleFinish = () => {
    setShowMessage(true);
    setTimeout(() => setShowMessage(false), 3000);
  };

  const toggleDone = (routines, setRoutines, id) => {
    setRoutines((prev) =>
      prev.map((r) =>
        r.id === id ? { ...r, done: !r.done, skipped: false } : r
      )
    );
  };

  const skipRoutine = (routines, setRoutines, id) => {
    setRoutines((prev) =>
      prev.map((r) =>
        r.id === id ? { ...r, skipped: !r.skipped, done: false } : r
      )
    );
  };

  const deleteRoutine = (routines, setRoutines, id) => {
    setRoutines((prev) => prev.filter((r) => r.id !== id));
  };

  const handleAddRoutine = (text, setText, routines, setRoutines) => {
    const trimmed = text.trim();
    if (trimmed === '') return;

    const newRoutine = {
      id: Date.now(),
      name: trimmed,
      done: false,
      skipped: false,
    };
    setRoutines([...routines, newRoutine]);
    setText('');
  };

  const RoutineBlock = ({ title, routines, setRoutines, input, setInput }) => (
    <div className="mb-12">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-semibold">{title}</h2>
        <button
          onClick={() => setIsEditing((prev) => !prev)}
          className="flex items-center text-sm text-blue-600 hover:underline"
        >
          <FaEdit className="mr-2" />
          {isEditing ? 'Done Editing' : 'Edit'}
        </button>
      </div>

      <div className="flex mb-6 space-x-2">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Add new routine..."
          className="flex-grow px-4 py-2 border rounded shadow-sm"
        />
        <button
          onClick={() => handleAddRoutine(input, setInput, routines, setRoutines)}
          className="flex items-center px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          <FaPlus className="mr-2" /> Add
        </button>
      </div>

      <div className="space-y-4">
        {routines.map((routine) => (
          <div
            key={routine.id}
            className={`flex items-center justify-between bg-white px-6 py-4 rounded shadow border-l-4 transition ${
              routine.done
                ? 'border-green-400 bg-green-50'
                : routine.skipped
                ? 'border-yellow-400 bg-yellow-50'
                : 'border-gray-300'
            }`}
          >
            <div className="flex items-center space-x-4">
              <input
                type="checkbox"
                checked={routine.done}
                onChange={() => toggleDone(routines, setRoutines, routine.id)}
                className="h-5 w-5"
              />
              <span
                className={`text-lg ${
                  routine.done ? 'line-through text-gray-400' : ''
                }`}
              >
                {routine.name}
              </span>
            </div>
            <div className="flex items-center space-x-3">
              {!routine.done && (
                <button
                  onClick={() => skipRoutine(routines, setRoutines, routine.id)}
                  className="text-sm text-yellow-500 hover:underline flex items-center"
                >
                  <FaForward className="mr-1" /> Skip
                </button>
              )}
              {isEditing && (
                <button
                  onClick={() => deleteRoutine(routines, setRoutines, routine.id)}
                  className="text-sm text-red-500 hover:underline flex items-center"
                >
                  <FaTrash className="mr-1" /> Delete
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <div className="min-h-screen px-20 py-10 bg-gray-100 text-gray-800 font-sans relative">
      {/* Notification */}
      {showMessage && (
        <div className="fixed top-6 right-6 bg-green-600 text-white px-4 py-2 rounded shadow-lg z-50">
          Routine complete! Great job!
        </div>
      )}

      {/* Morning */}
      <RoutineBlock
        title="Morning Routine"
        routines={morningRoutines}
        setRoutines={setMorningRoutines}
        input={newMorningText}
        setInput={setNewMorningText}
      />

      {/* Evening */}
      <RoutineBlock
        title="Evening Routine"
        routines={eveningRoutines}
        setRoutines={setEveningRoutines}
        input={newEveningText}
        setInput={setNewEveningText}
      />

      {/* Done Button */}
      <div className="mt-8 text-center">
        <button
          onClick={handleFinish}
          className="px-10 py-3 bg-blue-600 text-white text-lg rounded hover:bg-blue-700 shadow"
        >
          Done
        </button>
      </div>
    </div>
  );
};












