import React from 'react';
import {
    format,
    startOfWeek,
    addDays,
    subWeeks,
    addWeeks,
    isSameDay,
    isToday,
} from 'date-fns';
import { useState } from 'react';

export const Calendar = () => {
    const [weekOffset, setWeekOffset] = useState(0);
    const [addingDate, setAddingDate] = useState(null);
    const [newEventTitle, setNewEventTitle] = useState('');
    const [events, setEvents] = useState([]);

    const today = new Date();
    const startOfCurrentWeek = startOfWeek(addWeeks(today, weekOffset), { weekStartsOn: 1 });

    const weekDays = Array.from({ length: 14 }).map((_, index) =>
        addDays(startOfCurrentWeek, index)
    );

    const goToPreviousWeek = () => {
        setWeekOffset(weekOffset - 1);
    };

    const goToNextWeek = () => {
        setWeekOffset(weekOffset + 1);
    };

    return (
        <>
            <div className="flex justify-between items-center mb-6">
                <button
                    className="px-4 py-2 bg-gray-200 rounded hover:bg-gray-300"
                    onClick={goToPreviousWeek}
                >
                    ← Previous
                </button>
                <h1 className="text-2xl font-bold">
                    {format(startOfCurrentWeek, 'MMMM d')} - {format(addDays(startOfCurrentWeek, 13), 'MMMM d')}
                </h1>
                <button
                    className="px-4 py-2 bg-gray-200 rounded hover:bg-gray-300"
                    onClick={goToNextWeek}
                >
                    Next →
                </button>
            </div>
            <div className="grid grid-cols-7 gap-4">
                {weekDays.map((day, index) => (
                    <div
                        key={index}
                        className={`p-4 border rounded flex flex-col justify-start min-h-[120px] ${isToday(day) ? 'bg-green-100' : ''}`}
                    >
                        <div className="text-sm text-gray-500">
                            {format(day, 'EEE')}
                        </div>
                        <div className="text-xl font-semibold">
                            {format(day, 'dd')}
                            <div className="mt-2 text-sm text-left">
                                {events.filter(event => event.date === format(day, 'yyyy-MM-dd')).map((event, idx) => (
                                    <div
                                        key={idx}
                                        className="bg-blue-100 px-2 py-1 rounded mb-1 cursor-pointer break-words"
                                    >
                                        {event.title}
                                    </div>
                                ))}
                                <button
                                    className="text-xs text-blue-600 hover:underline mt-2"
                                    onClick={() => setAddingDate(format(day, 'yyyy-MM-dd'))}
                                >
                                    + Add
                                </button>
                                {addingDate === format(day, 'yyyy-MM-dd') && (
                                    <div className="mt-2">
                                        <input
                                            value={newEventTitle}
                                            onChange={(e) => setNewEventTitle(e.target.value)}
                                            placeholder="Event title"
                                            className="border px-2 py-1 text-sm rounded w-full mb-1"
                                        />
                                        <button
                                            onClick={() => {
                                                if (newEventTitle.trim() !== '') {
                                                    setEvents([
                                                        ...events,
                                                        { date: addingDate, title: newEventTitle.trim() }
                                                    ]);
                                                    setNewEventTitle('');
                                                    setAddingDate(null);
                                                }
                                            }}
                                            className="bg-blue-500 text-white text-xs px-2 py-1 rounded"
                                        >
                                            Save
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </>
    );
};
