import React, { useState, useEffect, useRef } from 'react';

const WORK_DURATION = 25 * 60; // 25 минут
const SHORT_BREAK = 5 * 60;    // 5 минут
const LONG_BREAK = 15 * 60;    // 15 минут
const CYCLES_BEFORE_LONG_BREAK = 4;

export const TimeMngr = () => {
  const [secondsLeft, setSecondsLeft] = useState(WORK_DURATION);
  const [isRunning, setIsRunning] = useState(false);
  const [mode, setMode] = useState('work'); // 'work', 'short', 'long'
  const [cycleCount, setCycleCount] = useState(0);
  const intervalRef = useRef(null);

  useEffect(() => {
    if (isRunning) {
      intervalRef.current = setInterval(() => {
        setSecondsLeft((prev) => {
          if (prev === 0) {
            clearInterval(intervalRef.current);
            handleTimerEnd();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(intervalRef.current);
  }, [isRunning]);

  const handleTimerEnd = () => {
    if (mode === 'work') {
      const newCycle = cycleCount + 1;
      setCycleCount(newCycle);
      if (newCycle % CYCLES_BEFORE_LONG_BREAK === 0) {
        setMode('long');
        setSecondsLeft(LONG_BREAK);
      } else {
        setMode('short');
        setSecondsLeft(SHORT_BREAK);
      }
    } else {
      setMode('work');
      setSecondsLeft(WORK_DURATION);
    }
    setIsRunning(false);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const handleStartPause = () => {
    setIsRunning((prev) => !prev);
  };

  const handleReset = () => {
    clearInterval(intervalRef.current);
    setIsRunning(false);
    setMode('work');
    setSecondsLeft(WORK_DURATION);
    setCycleCount(0);
  };

  return (
    <div className="flex flex-col items-center bg-red-600 text-white p-8 rounded-lg w-80 mx-auto mt-10">
      <h2 className="text-2xl font-bold mb-4 capitalize">{mode === 'work' ? 'Work' : mode === 'short' ? 'Short Break' : 'Long Break'}</h2>
      <div className="text-6xl font-mono mb-6">{formatTime(secondsLeft)}</div>
      <div className="flex space-x-4">
        <button
          className="bg-white text-red-600 px-4 py-2 rounded hover:bg-gray-200"
          onClick={handleStartPause}
        >
          {isRunning ? 'Pause' : 'Start'}
        </button>
        <button
          className="bg-white text-red-600 px-4 py-2 rounded hover:bg-gray-200"
          onClick={handleReset}
        >
          Reset
        </button>
      </div>
    </div>
  );
}
