import React, { useState, useEffect } from 'react';

export const Reminders = () => {
  const [reminders, setReminders] = useState([]);
  const [text, setText] = useState('');
  const [interval, setIntervalState] = useState(60); // Changed to setIntervalState
  const [start, setStart] = useState('09:00');
  const [end, setEnd] = useState('17:00');
  const [showPopup, setShowPopup] = useState(false);
  const [popupText, setPopupText] = useState('');

  const addReminder = () => {
    if (!text) return;
    setReminders([...reminders, {
      text,
      interval: parseInt(intervalState), // Use intervalState here
      start,
      end,
      lastTriggered: null
    }]);
    setText('');
  };

  useEffect(() => {
    let timerId; // Declare timerId outside useEffect's scope
    const checkReminders = () => {
      const now = new Date();
      const currentTime = now.getHours() * 60 + now.getMinutes();

      reminders.forEach((reminder, idx) => {
        const [startH, startM] = reminder.start.split(':').map(Number);
        const [endH, endM] = reminder.end.split(':').map(Number);
        const startMin = startH * 60 + startM;
        const endMin = endH * 60 + endM;

        if (currentTime >= startMin && currentTime <= endMin) {
          if (
            !reminder.lastTriggered ||
            currentTime - reminder.lastTriggered >= reminder.interval
          ) {
            setPopupText(reminder.text);
            setShowPopup(true);
            const timeoutId = setTimeout(() => setShowPopup(false), 5000); // hide after 5s

            setReminders(prev => {
              const updated = [...prev];
              updated[idx].lastTriggered = currentTime;
              return updated;
            });
          }
        }
      });
    };

    timerId = setInterval(checkReminders, 60000); // check every minute

    return () => {
      clearInterval(timerId); // Clear the interval
    };
  }, [reminders]);

  const setInterval = (value) => {
    setIntervalState(parseInt(value));
  };

  return (
    <div className="p-8 max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Reminders</h2>

      <div className="flex flex-col gap-2 mb-6">
        <input
          className="border p-2 rounded"
          placeholder="Reminder message"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <div className="flex gap-2">
          <input
            type="number"
            className="border p-2 rounded w-1/3"
            placeholder="Interval (min)"
            value={intervalState}
            onChange={(e) => setInterval(e.target.value)}
          />
          <input
            type="time"
            className="border p-2 rounded"
            value={start}
            onChange={(e) => setStart(e.target.value)}
          />
          <input
            type="time"
            className="border p-2 rounded"
            value={end}
            onChange={(e) => setEnd(e.target.value)}
          />
        </div>
        <button
          className="bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
          onClick={addReminder}
        >
          Add Reminder
        </button>
      </div>

      <ul className="space-y-2">
        {reminders.map((r, i) => (
          <li key={i} className="p-3 bg-gray-100 rounded shadow">
            <strong>{r.text}</strong> – every {r.interval} min (from {r.start} to {r.end})
          </li>
        ))}
      </ul>

      {showPopup && (
        <div className="fixed bottom-6 right-6 bg-yellow-100 border border-yellow-400 text-yellow-800 px-4 py-3 rounded shadow-lg animate-fade-in">
          ⏰ {popupText}
        </div>
      )}
    </div>
  );
};
