import React, { useState, useEffect } from 'react';

export const Reminders = () => {
  const [reminders, setReminders] = useState([]);
  const [text, setText] = useState('');
  const [interval, setInterval] = useState(60);
  const [start, setStart] = useState('09:00');
  const [end, setEnd] = useState('17:00');
  const [showPopup, setShowPopup] = useState(false);
  const [popupText, setPopupText] = useState('');

  const addReminder = () => {
    if (!text.trim()) return;
    setReminders(prev => [
      ...prev,
      {
        text,
        interval: parseInt(interval),
        start,
        end,
        lastTriggered: null,
      }
    ]);
    setText('');
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      const now = new Date();
      const nowMin = now.getHours() * 60 + now.getMinutes();

      setReminders(prevReminders => {
        return prevReminders.map(reminder => {
          const [startH, startM] = reminder.start.split(':').map(Number);
          const [endH, endM] = reminder.end.split(':').map(Number);
          const startMin = startH * 60 + startM;
          const endMin = endH * 60 + endM;

          if (
            nowMin >= startMin &&
            nowMin <= endMin &&
            (!reminder.lastTriggered || nowMin - reminder.lastTriggered >= reminder.interval)
          ) {
            setPopupText(reminder.text);
            setShowPopup(true);
            setTimeout(() => setShowPopup(false), 5000); // Hide after 5 seconds
            return { ...reminder, lastTriggered: nowMin };
          }

          return reminder;
        });
      });
    }, 10000); // check every 10 seconds for demo (change to 60000 in real)

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Reminders</h1>

      <div className="space-y-2 mb-6">
        <input
          className="border p-2 rounded w-full"
          placeholder="Reminder text"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <div className="flex gap-2">
          <input
            type="number"
            className="border p-2 rounded w-1/3"
            placeholder="Interval (min)"
            value={interval}
            onChange={(e) => setInterval(e.target.value)}
          />
          <input
            type="time"
            className="border p-2 rounded"
            value={start}
            onChange={(e) => setStart(e.target.value)}
          />
          <input
            type="time"
            className="border p-2 rounded"
            value={end}
            onChange={(e) => setEnd(e.target.value)}
          />
        </div>
        <button
          className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
          onClick={addReminder}
        >
          Add Reminder
        </button>
      </div>

      <ul className="space-y-2">
        {reminders.map((r, i) => (
          <li key={i} className="p-3 bg-gray-100 rounded shadow">
            {r.text} – every {r.interval} min (from {r.start} to {r.end})
          </li>
        ))}
      </ul>

      {showPopup && (
        <div className="fixed bottom-6 right-6 bg-yellow-100 border border-yellow-400 text-yellow-800 px-4 py-3 rounded shadow-lg">
          ⏰ {popupText}
        </div>
      )}
    </div>
  );
};
