import React from 'react'
import {
	format,
	startOfWeek,
	addDays
  } from 'date-fns';
  import { useState } from 'react';


export const Calendar = () => {
	const [weekOffset, setWeekOffset] = useState(0);
	const [addingDate, setAddingDate] = useState(null);
	const [newEventTitle, setNewEventTitle] = useState('');


	const today = new Date();
	const weekStart = startOfWeek(addDays(today, weekOffset * 7), { weekStartsOn: 1 });

	const weekDays = Array.from({length: 7}).map((_, index) => 
		addDays(weekStart, index)
	);

	const [events, setEvents] = useState([
		{ date: format(today, 'yyyy-MM-dd'), title: 'Написать диплом' },
		{ date: format(addDays(today, 1), 'yyyy-MM-dd'), title: 'Сходить в зал' },
	  ]);


  return (
	<>
		<div className="flex justify-between items-center mb-6">
			<button
			    className="px-4 py-2 bg-gray-200 rounded hover:bg-gray-300"
			    onClick={() => setWeekOffset(weekOffset - 1)}
			>
				← Previous
	        </button>
			<h1 className="text-2xl font-bold">
			    Week of {format(weekStart, 'MMMM d')}
			</h1>

			<button
				className="px-4 py-2 bg-gray-200 rounded hover:bg-gray-300"
			    onClick={() => setWeekOffset(weekOffset + 1)}
			>
				Next →
			</button>
		</div>
		<div className="grid grid-cols-7 gap-4">
			{weekDays.map((day, index) => (
		<div
		    key={index}
		      className="p-4 border rounded text-center"
		    >
		      <div className="text-sm text-gray-500">
		        {format(day, 'EEE')} {/* Пн, Вт, Ср */}
		      </div>
		      <div className="text-xl font-semibold">
		        {format(day, 'dd')}
		        <div className="mt-2 text-sm text-left">
			  {events
			    .filter(event => event.date === format(day, 'yyyy-MM-dd'))
			    .map((event, idx) => (
			      <div key={idx} className="bg-blue-100 px-2 py-1 rounded mb-1">
			        {event.title}
			      </div>
			    ))}
			</div>
		      </div>
		    </div>
		  ))}
		</div>
	</>
  )
}
