import React, { useState } from 'react';
import { FaEdit, FaTrash, FaCheck, FaForward, FaPlus } from 'react-icons/fa';

const initialMorning = [
  { id: 1, name: 'Wash your face', done: false, skipped: false },
  { id: 2, name: 'Make your bed', done: false, skipped: false },
];

const initialEvening = [
  { id: 101, name: 'Brush your teeth', done: false, skipped: false },
  { id: 102, name: 'Reflect on the day', done: false, skipped: false },
];

export const Routines = () => {
  const [morningRoutines, setMorningRoutines] = useState(initialMorning);
  const [eveningRoutines, setEveningRoutines] = useState(initialEvening);

  const [newMorningText, setNewMorningText] = useState('');
  const [newEveningText, setNewEveningText] = useState('');

  const [isEditing, setIsEditing] = useState(false);

  // Общие функции для toggle, skip, delete
  const toggleDone = (list, setList, id) => {
    setList((prev) =>
      prev.map((r) =>
        r.id === id ? { ...r, done: !r.done, skipped: false } : r
      )
    );
  };

  const skipRoutine = (list, setList, id) => {
    setList((prev) =>
      prev.map((r) =>
        r.id === id ? { ...r, skipped: !r.skipped, done: false } : r
      )
    );
  };

  const deleteRoutine = (list, setList, id) => {
    setList((prev) => prev.filter((r) => r.id !== id));
  };

  const addRoutine = (text, setText, list, setList, offset = 0) => {
    const trimmed = text.trim();
    if (trimmed === '') return;

    const newRoutine = {
      id: Date.now() + offset,
      name: trimmed,
      done: false,
      skipped: false,
    };

    setList([...list, newRoutine]);
    setText('');
  };

  const handleFinish = (message) => {
    alert(message);
  };

  // Компонент секции
  const RoutineSection = ({ title, routines, setRoutines, newText, setNewText, offset }) => (
    <div className="mb-12">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-3xl font-semibold">{title}</h2>
        <button
          onClick={() => setIsEditing((prev) => !prev)}
          className="flex items-center text-sm text-blue-600 hover:underline"
        >
          <FaEdit className="mr-2" />
          {isEditing ? 'Done Editing' : 'Edit'}
        </button>
      </div>

      <div className="flex mb-6 max-w-4xl mx-auto space-x-2">
        <input
          type="text"
          value={newText}
          onChange={(e) => setNewText(e.target.value)}
          placeholder="Add new routine..."
          className="flex-grow px-4 py-2 border rounded shadow-sm"
        />
        <button
          onClick={() => addRoutine(newText, setNewText, routines, setRoutines, offset)}
          className="flex items-center px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          <FaPlus className="mr-2" /> Add
        </button>
      </div>

      <div className="space-y-4 max-w-4xl mx-auto">
        {routines.map((routine) => (
          <div
            key={routine.id}
            className={`flex items-center justify-between bg-white px-6 py-4 rounded shadow border-l-4 transition ${
              routine.done
                ? 'border-green-400 bg-green-50'
                : routine.skipped
                ? 'border-yellow-400 bg-yellow-50'
                : 'border-gray-300'
            }`}
          >
            <div className="flex items-center space-x-4">
              <input
                type="checkbox"
                checked={routine.done}
                onChange={() => toggleDone(routines, setRoutines, routine.id)}
                className="h-5 w-5"
              />
              <span
                className={`text-lg ${
                  routine.done ? 'line-through text-gray-400' : ''
                }`}
              >
                {routine.name}
              </span>
            </div>
            <div className="flex items-center space-x-3">
              {!routine.done && (
                <button
                  onClick={() => skipRoutine(routines, setRoutines, routine.id)}
                  className="text-sm text-yellow-500 hover:underline flex items-center"
                >
                  <FaForward className="mr-1" /> Skip
                </button>
              )}
              {isEditing && (
                <button
                  onClick={() => deleteRoutine(routines, setRoutines, routine.id)}
                  className="text-sm text-red-500 hover:underline flex items-center"
                >
                  <FaTrash className="mr-1" /> Delete
                </button>
              )}
            </div>
          </div>
        ))}
      </div>

      <div className="mt-6 text-center">
        <button
          onClick={() => handleFinish(`${title} complete! Great job!`)}
          className="px-10 py-3 bg-blue-600 text-white text-lg rounded hover:bg-blue-700 shadow"
        >
          Done
        </button>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen px-20 py-10 bg-gray-100 text-gray-800 font-sans">
      <RoutineSection
        title="Morning Routine"
        routines={morningRoutines}
        setRoutines={setMorningRoutines}
        newText={newMorningText}
        setNewText={setNewMorningText}
        offset={0}
      />
      <RoutineSection
        title="Evening Routine"
        routines={eveningRoutines}
        setRoutines={setEveningRoutines}
        newText={newEveningText}
        setNewText={setNewEveningText}
        offset={10000}
      />
    </div>
  );
};









