import React, { useState } from 'react';
import {
  format,
  startOfWeek,
  addDays,
  isToday,
} from 'date-fns';

export const Calendar = () => {
  const [weekOffset, setWeekOffset] = useState(0);
  const [addingDate, setAddingDate] = useState(null);
  const [newEventTitle, setNewEventTitle] = useState('');
  const [events, setEvents] = useState([]);
  const [expandedEventDate, setExpandedEventDate] = useState(null);

  const today = new Date();
  const startOfCurrentWeek = startOfWeek(addDays(today, weekOffset * 7), { weekStartsOn: 1 });

  // generate 14 days (2 weeks)
  const calendarDays = [...Array(14)].map((_, index) =>
    addDays(startOfCurrentWeek, index)
  );

  const goToPreviousWeek = () => setWeekOffset(weekOffset - 1);
  const goToNextWeek = () => setWeekOffset(weekOffset + 1);

  const toggleExpand = (date) => {
    setExpandedEventDate((prev) => (prev === date ? null : date));
  };

  return (
    <div className="p-6">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <button onClick={goToPreviousWeek} className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300">&lt;</button>
        <h1 className="text-2xl font-bold">
          {format(startOfCurrentWeek, 'MMMM yyyy')} – {format(addDays(startOfCurrentWeek, 13), 'MMMM yyyy')}
        </h1>
        <button onClick={goToNextWeek} className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300">&gt;</button>
      </div>

      {/* Calendar Grid */}
      <div className="grid grid-cols-7 gap-4">
        {calendarDays.map((day, index) => {
          const dayStr = format(day, 'yyyy-MM-dd');
          const dayEvents = events.filter(event => event.date === dayStr);

          return (
            <div key={index} className={`p-3 border rounded h-64 flex flex-col justify-between ${isToday(day) ? 'border-blue-500' : ''}`}>
              {/* Date */}
              <div>
                <div className="text-sm text-gray-500 uppercase">{format(day, 'EEE')}</div>
                <div className="text-xl font-bold">{format(day, 'dd')}</div>
                <hr className="my-2" />
              </div>

              {/* Events */}
              <div className="flex-grow overflow-y-auto">
                {dayEvents.length > 0 && (
                  <div
                    onClick={() => toggleExpand(dayStr)}
                    className="cursor-pointer p-2 bg-gray-100 rounded text-sm hover:bg-gray-200 transition"
                  >
                    <strong>{dayEvents[0].title}</strong>
                    {expandedEventDate === dayStr && (
                      <div className="text-xs mt-1 text-gray-600">10:00 – 11:00 AM<br />{dayEvents[0].description}</div>
                    )}
                  </div>
                )}
              </div>

              {/* Add Event */}
              <div>
                {addingDate === dayStr ? (
                  <div className="mt-2 space-y-1">
                    <input
                      value={newEventTitle}
                      onChange={(e) => setNewEventTitle(e.target.value)}
                      placeholder="Event title"
                      className="border px-2 py-1 text-sm rounded w-full"
                    />
                    <button
                      onClick={() => {
                        if (newEventTitle.trim() !== '') {
                          setEvents([
                            ...events,
                            { date: dayStr, title: newEventTitle.trim(), description: 'No description yet' }
                          ]);
                          setNewEventTitle('');
                          setAddingDate(null);
                        }
                      }}
                      className="bg-blue-500 text-white text-xs px-2 py-1 rounded"
                    >
                      Save
                    </button>
                  </div>
                ) : (
                  <button
                    onClick={() => setAddingDate(dayStr)}
                    className="mt-2 text-xs text-blue-600 hover:underline"
                  >
                    + Add event
                  </button>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};
