import React from 'react';
import {
    format,
    startOfWeek,
    addDays,
    isToday,
} from 'date-fns';
import { useState } from 'react';

export const Calendar = () => {
    const [weekOffset, setWeekOffset] = useState(0);
    const [addingDate, setAddingDate] = useState(null);
    const [newEventTitle, setNewEventTitle] = useState('');
    const [events, setEvents] = useState([]);

    const today = new Date();
    const startOfCurrentWeek = startOfWeek(addDays(today, weekOffset * 7), { weekStartsOn: 1 });
    const weekDays = Array.from({ length: 7 }).map((_, index) =>
        addDays(startOfCurrentWeek, index)
    );

    const goToPreviousWeek = () => {
        setWeekOffset(weekOffset - 1);
    };

    const goToNextWeek = () => {
        setWeekOffset(weekOffset + 1);
    };

    return (
        <>
            {/* Header */}
            <div className="flex justify-between items-center mb-4">
                <button
                    className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
                    onClick={goToPreviousWeek}
                >
                    &lt;
                </button>
                <h1 className="text-3xl font-bold">
                    {format(startOfCurrentWeek, 'MMMM yyyy')}
                </h1>
                <button
                    className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
                    onClick={goToNextWeek}
                >
                    &gt;
                </button>
            </div>

            {/* Calendar Grid */}
            <div className="grid grid-cols-7 gap-4">
                {weekDays.map((day, index) => (
                    <div
                        key={index}
                        className={`p-3 border rounded flex flex-col h-64 ${isToday(day) ? 'border-blue-500' : ''}`}
                    >
                        {/* Day Header */}
                        <div className="text-sm font-thin uppercase">
                            {format(day, 'EEE')}
                        </div>
                        <div className="text-2xl font-bold">
                            {format(day, 'dd')}
                        </div>
                        <hr className="my-2" />

                        {/* Events List */}
                        <div className="flex-grow overflow-y-auto">
                            {events.filter(event => event.date === format(day, 'yyyy-MM-dd')).map((event, idx) => (
                                <div key={idx} className="mb-2">
                                    <div className="font-bold">{event.title}</div>
                                    <div className="text-xs text-gray-600">10:00 - 11:00 AM</div>
                                </div>
                            ))}
                            {/* "+3 more" link (example) */}
                            {events.filter(event => event.date === format(day, 'yyyy-MM-dd')).length > 2 && (
                                <div className="text-xs text-blue-500">+3 more</div>
                            )}
                        </div>

                        {/* Add Event Button */}
                        <button
                            className="mt-2 text-xs text-blue-600 hover:underline"
                            onClick={() => setAddingDate(format(day, 'yyyy-MM-dd'))}
                        >
                            + Add event
                        </button>

                        {/* Add Event Input (Conditional) */}
                        {addingDate === format(day, 'yyyy-MM-dd') && (
                            <div className="mt-2">
                                <input
                                    value={newEventTitle}
                                    onChange={(e) => setNewEventTitle(e.target.value)}
                                    placeholder="Event title"
                                    className="border px-2 py-1 text-sm rounded w-full mb-1"
                                />
                                <button
                                    onClick={() => {
                                        if (newEventTitle.trim() !== '') {
                                            setEvents([
                                                ...events,
                                                { date: addingDate, title: newEventTitle.trim() }
                                            ]);
                                            setNewEventTitle('');
                                            setAddingDate(null);
                                        }
                                    }}
                                    className="bg-blue-500 text-white text-xs px-2 py-1 rounded"
                                >
                                    Save
                                </button>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </>
    );
};
