import React from 'react';
import {
    format,
    startOfMonth,
    endOfMonth,
    addDays,
    subMonths,
    addMonths,
    isSameDay,
    isToday,
} from 'date-fns';
import { useState } from 'react';

export const Calendar = () => {
    const [currentMonth, setCurrentMonth] = useState(new Date());
    const [addingDate, setAddingDate] = useState(null);
    const [newEventTitle, setNewEventTitle] = useState('');
    const [events, setEvents] = useState([]);

    const startOfMonthDate = startOfMonth(currentMonth);
    const endOfMonthDate = endOfMonth(currentMonth);

    const daysInMonth = Array.from({ length: endOfMonthDate.getDate() }).map((_, index) =>
        addDays(startOfMonthDate, index)
    );

    const firstDayOfWeek = startOfMonthDate.getDay(); // 0 (Sun) to 6 (Sat)
    let leadingDays = [];
    if (firstDayOfWeek > 0) {
        leadingDays = Array.from({ length: firstDayOfWeek }).map((_, index) => null);
    }

    const lastDayOfWeek = endOfMonthDate.getDay();
    let trailingDays = [];
    if (lastDayOfWeek < 6) {
        trailingDays = Array.from({ length: 6 - lastDayOfWeek }).map((_, index) => null);
    }

    const allDays = [...leadingDays, ...daysInMonth, ...trailingDays];

    const goToPreviousMonth = () => {
        setCurrentMonth(subMonths(currentMonth, 1));
    };

    const goToNextMonth = () => {
        setCurrentMonth(addMonths(currentMonth, 1));
    };

    return (
        <>
            <div className="flex justify-between items-center mb-6">
                <button
                    className="px-4 py-2 bg-gray-200 rounded hover:bg-gray-300"
                    onClick={goToPreviousMonth}
                >
                    ← Previous
                </button>
                <h1 className="text-2xl font-bold">
                    {format(currentMonth, 'MMMM yyyy')}
                </h1>
                <button
                    className="px-4 py-2 bg-gray-200 rounded hover:bg-gray-300"
                    onClick={goToNextMonth}
                >
                    Next →
                </button>
            </div>
            <div className="grid grid-cols-7 gap-4">
                {allDays.map((day, index) => (
                    <div
                        key={index}
                        className={`p-4 border rounded flex flex-col justify-start min-h-[120px] ${day ? '' : 'bg-gray-100'
                            } ${day && isToday(day) ? 'bg-green-100' : ''}`}
                    >
                        {day ? (
                            <>
                                <div className="text-sm text-gray-500">
                                    {format(day, 'EEE')}
                                </div>
                                <div className="text-xl font-semibold">
                                    {format(day, 'dd')}
                                    <div className="mt-2 text-sm text-left">
                                        {events.filter(event => event.date === format(day, 'yyyy-MM-dd')).map((event, idx) => (
                                            <div
                                                key={idx}
                                                className="bg-blue-100 px-2 py-1 rounded mb-1 cursor-pointer break-words"
                                            >
                                                {event.title}
                                            </div>
                                        ))}
                                        <button
                                            className="text-xs text-blue-600 hover:underline mt-2"
                                            onClick={() => setAddingDate(format(day, 'yyyy-MM-dd'))}
                                        >
                                            + Add
                                        </button>
                                        {addingDate === format(day, 'yyyy-MM-dd') && (
                                            <div className="mt-2">
                                                <input
                                                    value={newEventTitle}
                                                    onChange={(e) => setNewEventTitle(e.target.value)}
                                                    placeholder="Event title"
                                                    className="border px-2 py-1 text-sm rounded w-full mb-1"
                                                />
                                                <button
                                                    onClick={() => {
                                                        if (newEventTitle.trim() !== '') {
                                                            setEvents([
                                                                ...events,
                                                                { date: addingDate, title: newEventTitle.trim() }
                                                            ]);
                                                            setNewEventTitle('');
                                                            setAddingDate(null);
                                                        }
                                                    }}
                                                    className="bg-blue-500 text-white text-xs px-2 py-1 rounded"
                                                >
                                                    Save
                                                </button>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            </>
                        ) : (
                            <div></div>
                        )}
                    </div>
                ))}
            </div>
        </>
    );
};
